package rc.control;

import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.SQLException;

import javax.naming.Context;
import javax.naming.InitialContext;
import javax.naming.NamingException;
import javax.servlet.ServletContextEvent;
import javax.servlet.ServletContextListener;
import javax.sql.DataSource;

import java.io.ByteArrayInputStream;
import java.io.InputStream;
import java.util.Base64;

public class MyServletContextListener implements ServletContextListener {
    @Override
    public void contextInitialized(ServletContextEvent sce) {
    	Connection connection = null;
    	Context initCtx;
		try {
			initCtx = new InitialContext();
			Context envCtx = (Context) initCtx.lookup("java:comp/env");
	    	DataSource ds = (DataSource) envCtx.lookup("jdbc/retrocrates");
	        connection = ds.getConnection();
		} catch (NamingException e) {
			// TODO Auto-generated catch block
			System.out.println(e.getMessage()); 
		} catch (SQLException e) {
			// TODO Auto-generated catch block
			System.out.println(e.getMessage()); 
		}
	    PreparedStatement preparedStatement = null;
	    String insertSQL = "UPDATE Prodotto SET Foto = ?";
	    try {
	        preparedStatement = connection.prepareStatement(insertSQL);
	        String Base = "";
	        byte[] decodedBytes = Base64.getDecoder().decode(Base);
	        InputStream binaryStream = new ByteArrayInputStream(decodedBytes);
	        preparedStatement.setBinaryStream(1, binaryStream);
	        preparedStatement.executeUpdate();
	        connection.commit();
	        
	    }
	    catch(Exception e) {
	    	System.out.println(e.getMessage());    
	    	}finally {
	        try {
	            if (preparedStatement != null)
	                preparedStatement.close();
	        } catch (SQLException e) {
	        	System.out.println(e.getMessage());  
			} finally {
	            if (connection != null)
					try {
						connection.close();
					} catch (SQLException e) {
						// TODO Auto-generated catch block
						e.printStackTrace();
					}
	        }
	    }
    }

    @Override
    public void contextDestroyed(ServletContextEvent sce) {
        // Codice da eseguire alla chiusura dell'applicazione
    }
}
